import React, { useEffect } from "react";
import "../../styles/Home.css";
import myPicture from "../../images/my-picture.jpeg";
import "bootstrap/dist/css/bootstrap.min.css";
import { Particles } from 'react-tsparticles'
import { loadTrianglesPreset} from 'tsparticles-preset-triangles'


export default function Home() {
    useEffect(() => {
      loadTrianglesPreset()
    }, [])
  return (
    <div className="background">

      <div className="page-wrapper">
      <Particles
        className="particles"
        options={{
          fpsLimit: 60,
          particles: {
            number: {
              value: 80,
            },
            links: {
              enable: true,
              distance: 100,
              color: "#ffffff",
              opacity: 0.5,
              width: 1,
            },
            move: {
              enable: true,
              speed: 3,
            },
            shape: {
              type: 'polygon',
              options: {
                polygon: {
                  sides: 3,
                },
              },
            },
          },
          interactivity: {
            events: {
              onHover: {
                enable: true,
                mode: 'repulse',
              },
            },
          },
        }}
      />
        <div className="container">
          <h1 className="header text-center mt-4">Santiago's Portfolio</h1>
          <div className="about-me d-flex justify-content-center align-items-center mt-5">
            <img src={myPicture} alt="Profile" className="rounded-circle me-5" />
            <p className="text-wrapper">
              Hi, my name is Santiago and I'm a web developer. Lorem ipsum dolor sit
              amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt
              ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis
              nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo
              consequat. Duis aute irure dolor in reprehenderit in voluptate velit
              esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat
              cupidatat non proident, sunt in culpa qui officia deserunt mollit anim
              id est laborum.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}
